import { useEffect, useState } from "react";
import { Container } from "./styles";
import { instance } from "../../services/instanceAxios";


interface Transaction {
    id: number;
    title: string;
    type: string;
    category: string;
    amount: number;
    createdAt: string;
}


export const TransactionTable = () => {

    const [transactions, setTransactions] = useState<Transaction[]>([]);

    useEffect(() => {
        instance.get('/transactions').then(response => setTransactions(response.data.transactions));
    }, []);

    const currencyFormat = new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' });
    const dateFormat = new Intl.DateTimeFormat('pt-BR');

    return (
        <Container>
            <table>
                <thead>
                    <tr>
                        <th>Titulo</th>
                        <th>Pre√ßo</th>
                        <th>Categoria</th>
                        <th>data</th>
                    </tr>
                </thead>

                <tbody>
                    { transactions.map(transaction => (
                        <tr key={transaction.id}>
                            <td className="title">{ transaction.title }</td>
                            <td className={ transaction.type }> { currencyFormat.format(transaction.amount) }</td>
                            <td>{ transaction.type }</td>
                            <td>{ dateFormat.format(new Date) }</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </Container>
    );
}
