import styled from "styled-components";


export const Container = styled.section`

    display: grid;
    grid-template-rows: repeat(4, 1fr);
    margin: 0 auto;
    width: 100%;
    height: 100vh;
    max-width: 1120px;

    margin-top: 3rem;

    table {
        width: 100%;
        height: 100%;
    }

    thead {
        width: 100%;
        border-spacing: 0 0.5rem;
    }
`
