export const currencyFormatter = (value: number) => {
    new Intl.NumberFormat('pt-BR', {
        style: 'currency', currency: 'BRL' 
    }).format(value);
}


export const dateFormatter = (date: string) => {
    new Intl.DateTimeFormat('pt-BR').format(new Date(date));
}
