import styled from "styled-components";


export const Container = styled.section`

    margin-top: 3rem;

    table {
        width: 100%;
        border-spacing: 0 0.5rem;


        th {
            color: var(--text-body);
            font-weight: 400;
            padding: 1rem 1rem;
            text-align: left;
        }

        tbody {
            width: 100%;
        }
    }

`
